const label=$(`<div class="label"></div>`);$(document).on("load",loadLabels);if($(".label").length===0){$("body").append(label)}$(document).on("mousemove",e=>{const hoverElement=$(e.target);if(hoverElement.attr("data-title")){show(hoverElement)}else{hide()}});function loadLabels(){$(`[title]`).each((i,element)=>{const el=$(element);el.attr("data-title",el.attr("title"));el.attr("title",null)})}function show(element){const title=element.attr("data-title");label.html(title);let x=element.offset().left+element.width()/2-label.width()/2;let y=element.offset().top+element.height()+44;if(x+label.width()>window.innerWidth){x=window.innerWidth-label.width()-10}if(y+label.height()+16>=window.innerHeight){y=element.offset().top-label.height()-20}label.css({top:y,left:x,opacity:1})}function hide(){label.css({opacity:0})}$("*").on("scroll",e=>{hide()});